/* body */

:focus{outline:0}
*{margin:0;padding:0;-webkit-box-sizing:border-box;-moz-box-sizing:border-box;box-sizing:border-box}

html,body{cursor:url(https://dorew-site.github.io/assets/ring/body/body.cur),default!important}
a,a:hover,textarea,input{cursor:url(https://i.imgur.com/F7yuaFW.png),progress}

html{background-repeat:repeat-x;background-color:#C0DEED;padding:0;margin:auto}
body{background-color:#FFF;margin:auto;max-width:800px;font-family:'basic-vi';font-size:13px;line-height:1.4;padding:0;color:#343434;border:4px solid #FFF;word-wrap:break-word}

.header,.footer{font-size:14px;font-weight:400;text-transform:capitalize;color:#fff;background:#000;background-image:url(https://i.imgur.com/cLYSxSb.gif);padding:8px}
.header{border-radius:5px 5px 0 0;border-bottom:3px solid red}
.footer{border-radius:0 0 5px 5px;margin-top:6px;border-top:3px solid red}
.is_logo{font-family:'fiolex-vi';font-size:40px;color:#fa016d;-webkit-animation:hue 3s linear infinite;text-shadow:2px 0 0 #fff,-2px 0 0 #fff,0 2px 0 #fff,0 -2px 0 #fff,1px 1px #fff,-1px -1px 0 #fff,1px -1px 0 #fff,-1px 1px 0 #fff}
.maintxt{border:2px solid #f3f3f3;margin:0;padding:4px}

@-webkit-keyframes hue { 100%{ -webkit-filter:hue-rotate(360deg);} } 

/* media */

a,a:active {color:green;text-decoration:none}
a:hover{color: red;transition: all 2s ease;-webkit-transition: all 2s ease;-moz-transition: all 2s ease;position: relative;text-shadow: 0 0 3px orange;background-image: url("https://i.imgur.com/NNsItzO.gif")}
hr{border-color:#cecece;border-style:dotted;border-width:1px 0 0;margin:0}p{margin-bottom:6px;margin-top:6px}ul{margin:0;padding-left:20px}
img{max-width:90%}iframe{max-width:98%}

.video-wrapper{position:relative;padding-bottom:56.25%;height:0;overflow:hidden}.video-wrapper iframe{position:absolute;top:0;left:0;width:100%;height:100%}.kvideo{max-width:80%;max-height:auto}
.thumb{float:left;margin:5px 5px 0 0;padding:1px;height:50px;width:50px;-webkit-border-radius:30px;-moz-border-radius:30px;border-radius:30px;border:1px solid #ccc;-webkit-transition:all .2s ease-in;-moz-transition:all .2s ease-in;-o-transition:all .2s ease-in;transition:all .2s ease-in}.thumb:hover{-webkit-border-radius:0;-moz-border-radius:0;border-radius:0;-webkit-transition:all .2s ease-out;-moz-transition:all .2s ease-out;-o-transition:all .2s ease-out;transition:all .2s ease-out}
.thumbnail{float:left;margin:5px 5px 0 0;padding:1px;height:50px;width:50px;-webkit-border-radius:30px;-moz-border-radius:30px;border-radius:30px;border:1px solid #d8d8d8;-webkit-transition:all .2s ease-in;-moz-transition:all .2s ease-in;-o-transition:all .2s ease-in;transition:all .2s ease-in}.thumbnail:hover{-webkit-border-radius:0;-moz-border-radius:0;border-radius:0;-webkit-transition:all .2s ease-out;-moz-transition:all .2s ease-out;-o-transition:all .2s ease-out;transition:all .2s ease-out}
.sub{border-top:1px dotted #dddedf;margin-top:4px;padding-top:6px}.sub2{border-top:1px dotted #dddedf;font-size:12px;margin-top:4px;padding-top:6px}

/* tieu de - list */

.phdr{background-color:green;font-weight:700;margin:6px 0 0;color:#FFF;padding-right:6px;padding-left:8px;padding-top:7px;padding-bottom:7px}.phdr:hover {background: #000;transition: all 3s ease;-webkit-transition: all 3s ease;
  -moz-transition: all 3s ease;position: relative;text-shadow: 0 0 3px #ff0;}.phdr a:link,.phdr a:visited{color:#ffa;font-weight:700;text-decoration:none}.phdr a:hover{color:#e8d200;text-decoration:none}
.menu{background-color:#FFF;border-color:#D5D5D5;border-style:solid;border-width:1px;margin:2px 0 0;padding:6px}
