#menu1{background:#099;border-bottom:1px solid #D1D1D1;margin-bottom:4px;border-radius:0 0 0 0}#menu1 a{color:#fff}#menu1 ul{padding-left:5px}#menu1 li{display:inline-block;padding:0;margin-left:1px}#menu1 li a{display:block;padding:6px 4px 4px 0;font-family:'basic-vi';font-size:12px}
.list{color:#000;background:none repeat scroll 0 0 #fff;line-height:24px;border-left:1px solid #d1d3d9;border-right:1px solid #d1d3d9;border-bottom:1px solid #d1d3d9;padding:5px 7px}.list:hover{background:none repeat scroll 0 0 #fff;line-height:24px;border-left:1px solid #d1d3d9;border-right:1px solid #d1d3d9;border-bottom:1px solid #d1d3d9;padding:5px 7px;border-left:3px solid red}
.list1{background-color:#DFFBE1;border:1px solid #c6edde;margin:1px 0 0;padding:6px}.list2{background-color:#FFF;border-color:#D5D5D5;border-style:solid;border-width:0 1px 1px;margin:0;padding:6px}.list3{background-color:#FFF;border-color:#D5D5D5;border-style:solid;border-width:0 1px 1px;margin:0;padding:6px}.list3:hover{background-color:#F3F3F3;border:1px solid #D5D5D5}
.new-topic{background-color:#FF5252;border:1px solid #D32F2F;color:#FFF;line-height:14px;vertical-align:baseline;white-space:nowrap;padding:1px 4px 2px}.all-topic{background-color:#fff;border:1px solid #C0BFBF;color:#565656;line-height:14px;vertical-align:baseline;white-space:nowrap;padding:3px 4px;margin-right:4px}
.func{border-left:2px solid #423456;color:#423456;margin-top:4px;margin-left:2px;padding-left:4px}
.gmenu{background-color:#f0fffa;border:1px solid #c6edde;margin:1px 0 0;padding:6px}.rmenu{background-color:#ffd1f0;border:1px solid #ffb3dc;margin:1px 0 0;padding:6px;overflow:auto}.topmenu{background-color:#fdfdfd;color:#456789;border:1px solid #d7d7d7;padding:4px 6px;overflow:auto}
.news{background-color:#eee;border:1px solid #fff;color:#333;padding:3px 4px 4px}.news:hover{background:#fffdcc;border:1px solid #ffda19;border-radius:3px;padding:4px}.notice{padding:4px;border:1px solid;margin-top:4px;background:#FFC;color:red;border-color:#FFD324;text-align:left}
.trithuc9x{color:#000;background:none repeat scroll 0 0 #fff;line-height:24px;border-left:1px solid #CFC;border-right:1px solid #CFC;border-bottom:1px solid #CFC;padding:5px 7px}.trithuc9x:hover{color:#000;background:none repeat scroll 0 0 #CFC;line-height:24px;border-left:1px solid #fff;border-right:1px solid #fff;border-bottom:1px solid #fff;padding:5px 7px}
.danhmuc{background-color:#Ffe;background-image:url(https://i.imgur.com/UOrb0ma.gif);background-repeat:repeat-x;border-left:1px solid #eeecd9;border-right:1px solid #eeecd9;border-bottom:1px solid #eeecd9;margin:1px 0 0;padding:6px}
.dangcap{background-color:#f0fffa;border:1px solid #c6edde;margin:1px 0 0;padding:6px}.trexanh{background:none repeat scroll 0 0 #FFF;line-height:24px;border:1px solid #d5d5d5;padding:3px 4px 4px}.trexanh:hover{background:none repeat scroll 0 0 #FF9;line-height:24px;border:1px solid #f09;padding:3px 4px 4px}

/* can le */

.icon{padding:3px 6px 3px 1px;vertical-align:middle}.icon-inline{margin-left:2px;padding-right:4px;vertical-align:middle}.icon-l5{padding-left:5px;vertical-align:middle}.icon-r5{padding-right:5px;vertical-align:middle}.icon-l3{padding-left:3px;vertical-align:middle}.icon-r3{padding-right:3px;vertical-align:middle}.icon-tb{padding-left:3px;padding-right:3px;vertical-align:middle}
.rightf{float:right;vertical-align:middle;margin:1px;padding:1px}.center{text-align:center}.right{text-align:right}.left{float:left}

/* dinh dang van ban */

h1,h2,h3,h4,h5,h6 {display:inline;font-size:13px}
h3{margin:0;padding:0 0 2px}h4{margin:6px 0 2px}
em{font-style:italic}strong{font-weight:600}

.clip a:link,.clip a:visited{color:#424e59}
.green,.green a:link,.green a:visited{color:green}
